        } else if (this.form.secondary == '') {
          this.$message('请选择次坐标轴');
        } else {
          this.columnDataNPL(this.domIndex, this.cartName, this.form);
          this.closeEdit(this.domIndex);
        }
      }
    },
    strutTable: function strutTable(index, el) {
      var dis = document.getElementsByClassName('rightChartBox' + index)[0].style.width;

      if (dis == '250px' || dis == '') {
        document.getElementsByClassName('rightChartBox' + index)[0].style.width = '0';
        document.getElementsByClassName('leftTableBox' + index)[0].style.width = '100%';
      } else {
        document.getElementsByClassName('rightChartBox' + index)[0].style.width = '250px';
        document.getElementsByClassName('leftTableBox' + index)[0].style.width = 'calc(100% - 250px)';
      }

      if (el.target.innerHTML == '展开') {
        el.target.innerHTML = '收起';
      } else {
        el.target.innerHTML = '展开';
      }
    }
  }
};
