import dataGrid from '@/components/common/DataGrid.vue';
export default {
  components: {
    dataGrid: dataGrid
  },
  data: function data() {
    var validator = function validator(rule, value, callback) {
      var test = /^(?![0-9]+$)(?![a-zA-Z]+$)[0-9A-Za-z]{8,20}$/;
      var testEmail = /^[a-zA-Z0-9]+([-_.][a-zA-Z0-9]+)*@[a-zA-Z0-9]+([-_.][a-zA-Z0-9]+)*\.[a-z]{2,}$/;
      var testTel = /^1[3456789]\d{9}$/;

      if (rule.field == 'Email') {
        if (value === '') {
          callback(new Error('请输入邮箱'));
        } else if (!testEmail.test(value)) {
          callback(new Error('请输入正确格式的邮箱地址'));
        } else {
          callback();
        }
      } else if (rule.field == 'telephone') {
        if (value === '') {
          callback(new Error('请输入手机号'));
        } else if (!testTel.test(value)) {
          callback(new Error('请输入正确格式的手机号'));
        } else {
          callback();
        }
      } else {
        if (value === '') {
          callback(new Error('请输入密码'));
        } else if (!test.test(value)) {
          callback(new Error('密码长度为8-20位，必须包括字母、数字'));
        } else {
          callback();
        }
      }
    };

    return {
      listOrder: 'ItemId asc,TrustStartDate asc',
      listFilter: 'IsMarketProduct = 1',
      searchS: true,
      fields: [],
      checkedData: [],
      tableDataList: [],
      viewType: 'DV.view_Products',
      AddUserDialog: false,
      EditUserDialog: false,
      title: '',
      passwordForm: {
        userName: '',
        DisplayName: '',
        Email: '',
        telephone: '',
        newPassword: '',
        confirmPassword: '',
        Orgenaration: '0'
      },
      passwordRules: {
        Email: [{
          validator: validator,
          trigger: 'blur'
        }],
        telephone: [{
          validator: validator,
          trigger: 'blur'
        }],
        newPassword: [{
          validator: validator,
          trigger: 'blur'
        }],
        confirmPassword: [{
          validator: validator,
          trigger: 'blur'
        }]
      },
      passwordErrors: {
        Email: '',
        telephone: '',
        confirmPassword: ''
      }
    };
  },
  computed: {},
  created: function created() {
    this.getOrganData();
  },
  methods: {
    getOrganData: function getOrganData() {
      var _this = this;

      var svc = this.$http('PortfolioManagement', 'DV.AdvanceFilter_GetSettingByEnterPrise1', true);
      svc.AddParam({
        Name: 'TemplateName',
        Value: '',
        DBType: 'string'
      });
      svc.ExecTables().then(function (response) {
        if (!response || response.length < 1) {
          return;
        }

        _this.fields = JSON.parse(JSON.stringify(response[0]));
      });
    },
    EditEnterprise: function EditEnterprise(row) {
      this.AddUserDialog = true;
      this.EditUserDialog = false;
      this.title = '编辑用户';
      var Current = [];
      Current.userName = row.TrustNameShort;
      Current.DisplayName = row.TrustCode;
      Current.Email = '12345@163.com';
      Current.telephone = '15885246789';
      Current.Orgenaration = row.TrustName;
      this.passwordForm = Current;
    },
    deleteEnterprise: function deleteEnterprise(TrustID) {
      this.$message.success('删除成功');
    },
    showAddUser: function showAddUser() {
      var self = this;
      self.EditUserDialog = true;
      self.AddUserDialog = true;
      self.TrustId = 0;
      this.title = '新增用户';
      this.passwordForm.userName = '';
      this.passwordForm.DisplayName = '';
      this.passwordForm.Email = '';
      this.passwordForm.telephone = '';
      this.passwordForm.Orgenaration = '0';
      this.passwordForm.newPassword = '';
      this.passwordForm.confirmPassword = '';
    },
    addUser: function addUser() {},
    row_key: function row_key(row) {
      //console.log(row)
      return this.viewType === 'DV.view_Products' ? row.TrustId : row.SNO;
    },
    doListSortChange: function doListSortChange(orderBy) {
      this.listOrder = orderBy ? orderBy : 'TrustId desc';
    },
    //选中产品
    selectRow: function selectRow(data) {
      console.log(data);
      this.checkedData = data;
    },
    listDataChanged: function listDataChanged(data) {
      this.tableDataList = data;
    }
  },
  mounted: function mounted() {
    this.$title();
    this.listFilter = "IsMarketProduct = 1";
  }
};
