import dataGrid from '@/components/common/DataGrid.vue';
import CreateTrust from "@/components/admin/CreateTrust";
import { BasePath } from '@/utils/config.js';
export default {
  components: {
    dataGrid: dataGrid,
    CreateTrust: CreateTrust
  },
  data: function data() {
    return {
      listOrder: 'ItemId asc,TrustStartDate asc',
      listFilter: 'IsMarketProduct = 1',
      searchS: true,
      fields: [],
      removeShow: false,
      searchCon: false,
      bondCode: '',
      checkedData: [],
      tableDataList: [],
      viewType: 'DV.view_Products',
      addRole: false,
      roleForm: {
        roleName: '',
        roleDesc: ''
      },
      title: ''
    };
  },
  computed: {
    userName: function userName() {
      return this.$store.state.user.userName;
    },
    ConcernTrust: function ConcernTrust() {
      return this.$store.state.productInfo.ConcernTrust;
    }
  },
  methods: {
    searchSC: function searchSC() {
      this.searchS = !this.searchS;
    },
    EditRole: function EditRole(row) {
      this.addRole = true;
      this.title = '编辑角色';
      this.roleForm.roleName = row.TrustName;
      this.roleForm.roleDesc = '描述信息';
    },
    deleteRole: function deleteRole(row) {
      this.$message.success('删除成功');
    },
    showAddRole: function showAddRole() {
      var self = this;
      self.addRole = true;
      this.roleForm.roleName = '';
      this.roleForm.roleDesc = '';
      this.title = '新增角色';
    },
    addRoleFunc: function addRoleFunc() {},
    row_key: function row_key(row) {
      //console.log(row)
      return this.viewType === 'DV.view_Products' ? row.TrustId : row.SNO;
    },
    doListSortChange: function doListSortChange(orderBy) {
      this.listOrder = orderBy ? orderBy : 'TrustId desc';
    },
    //选中产品
    selectRow: function selectRow(data) {
      console.log(data);
      this.checkedData = data;
    },
    listDataChanged: function listDataChanged(data) {
      this.tableDataList = data;
    }
  },
  mounted: function mounted() {
    this.$title();
    this.listFilter = "IsMarketProduct = 1";
  }
};
