.repayment-matrix{
  position: relative;
  width: 250px;
  height: 160px;
  margin-bottom: 10px;
  display: inline-block;
  .layered-container{
    display: flex;
    flex-direction: column;

    &.is-horizontal{
      flex-direction: row;
    }
  }

  .layered{
    position: relative;
    border: 1px solid #fff;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
    height: 100%;
    font-size: 12px;
    color: #fff;
    text-align: center;

    .layered-used-space{
      position: absolute;
      top: 0;
      right: 0;
      left: 0;
      opacity: 1;
      pointer-events: none;
      background: url(data:image/png;base64,) repeat;
    }
  }

  .layered-info{
    position: absolute;
    width: 130px;
    padding: 5px;
    background-color: #fff;
    font-size: 12px;
    line-height: 1.77;
    color: #555;
    z-index: 9999;
    box-shadow: 0 0 3px 0 rgba(0, 0, 0, .2);

    p {
      span{
        color: #999;
        margin-right: 3px;
      }
    }
  }
}
