import ItemBox from '@/components/layout/ItemBox.vue';
import HChart from '@/components/common/Highcharts';
export default {
  data() {
    return {
      scheduleData: [],
      CurrentPeriodSel: 1,
      chart1: null,
      chart2: null,
      form: {
          StartTime: '',
          EndTime: ''
        }
    };
  },
  components: {
    ItemBox,
    HChart
  },
  created() {
    this.CurrentPeriodSel = this.CurrentPeriodAr.length > 0 ? this.CurrentPeriodAr[0] : 0;
    this.initSchedule(this.CurrentPeriodSel);
    this.initCompareChart();
  },
  computed: {
    CurrentPeriodAr() {
      var CurrentPeriodAr = [];
      var CurrentPeriod = Number(this.$route.query.CurrentPeriod);
      if (CurrentPeriod || CurrentPeriod == 0) {
        for (var i = 0; i < CurrentPeriod + 1; i++) {
          CurrentPeriodAr.push(i);
        }
      }
      return CurrentPeriodAr;
    },
    TrustID() {
      return this.$store.state.productInfo.trustId;
    }
  },
  methods: {
    PeriodChange(value) {
      this.initSchedule(value);
    },
    initSchedule(CurrentPeriodSel) {
      var PaymentPeriodID;
      if (CurrentPeriodSel) {
        PaymentPeriodID = CurrentPeriodSel;
      } else {
        PaymentPeriodID = this.CurrentPeriodSel;
      }
      var self = this;
      var svc = this.$http('PortfolioManagement', 'DV.PaymentScheduleAggregation_Get');
      svc.AddParam({
        Name: 'TrustID',
        Value: self.TrustID,
        //Value: 9832,
        DBType: 'int'
      });
      svc.AddParam({
        Name: 'PaymentPeriodID',
        Value: PaymentPeriodID, //4513,
        DBType: 'int'
      });
      svc.ExecTable().then(res => {
        this.scheduleData = res;
      });
    },
     productSubjectChange() {
        this.initCompareChart();
      },
    initCompareChart() {
     let IsTopUpAvailable=null
      Promise.all([this.getDataSource1(), this.getDataSource2()]).then(res => {
        let date1 = res[0].map(v => v.startDate),
          date2 = res[1].map(v => v.startDate);
             let point =1,point2 =1,point3 =1,point4 =1
           for (var i = 0; i < res[0].length; i++) {
             //循环购买的产品，直接把这个资产池剩余去掉，只留资产池回收对比,目前获取的数据还不能算说明书的剩余
               IsTopUpAvailable= res[0][i].IsTopUpAvailable
               if(res[0][i].startDate==res[0][i].TrustStartDate)
               (
                    point=res[0][i].rn
               )
                if(res[0][i].startDate==res[0][i].PoolCloseDate)
               (
                  point2=res[0][i].rn
               )

           }

           for (var i = 0; i < res[1].length; i++) {
               if(res[1][i].startDate==res[1][i].TrustStartDate)
               (
                    point3=res[1][i].rn
               )
                if(res[1][i].startDate==res[1][i].PoolCloseDate)
               (
                  point4=res[1][i].rn
               )

           }

        let dates = Array.from(new Set([].concat(date1, date2)));
        this.chart1 = {
          chart: {
            type: 'line'
          },
          title: {
            text: '资产池回收款'
          },
          subtitle: {
            text: null
          },
          xAxis: {
            categories: dates,
            crosshair: true
          },
          yAxis: {
            title: {
              text: ''
            },
            labels : {
              format:'{value} 亿元'
            },
          },
            plotOptions: {
                series: {
                    marker: {
                        radius: 3, //曲线点半径，默认是4
                        symbol: 'diamond' //曲线点类型：”circle”, “square”, “diamond”, “triangle”,”triangle-down”，默认是”circle”
                    }
                }
            },
          tooltip: {
            shared: true
          },
          series: [
            {
              name: '说明书',
              data: res[0].map(v => v.RemainingPrincipal),
              zoneAxis: 'x',
              zones: [{
                      value: point,
                      color: '#7cb5ec',
                      }, {
                      value: point2,
                      color: '#90ed7d'
                      },{
                      color: '#7cb5ec'
                    }]

            },
            {
              name: '实际',
              data: res[1].map(v => v.RemainingPrincipal),
              zoneAxis: 'x',
              zones: [{
                      value: point,
                      color: '#000000',
                      }, {
                      value: point2,
                      color: '#90ed7d'
                      },{
                      color: '#000000'
                    }]
            }
          ]
        };
        this.chart2 = {
          chart: {
            type: 'line'
          },
          title: {
            text: '资产池剩余'
          },
          subtitle: {
            text: null
          },
          xAxis: {
            categories: dates,
            crosshair: true
          },
          yAxis: {
            title: {
              text: ''
            },
            labels : {
              format:'{value} %'
            },
            max: 100
          },
          tooltip: {
            // head + 每个 point + footer 拼接成完整的 table
            headerFormat: '<span style="font-size:10px">{point.key}</span><table>',
            pointFormat: '<tr><td style="color:{series.color};padding:0">{series.name}: </td>' +
            '<td style="padding:0"><b>{point.y} %</b></td></tr>',
            footerFormat: '</table>',
            shared: true,
            useHTML: true
          },
          series: [
            {
              name: '说明书',
              data: res[0].map(v => parseFloat(v.Percentage)),
              zoneAxis: 'x',
              zones: [{
                      value: point3,
                      color: '#7cb5ec',
                      }, {
                      value: point4,
                      color: '#90ed7d'
                      },{
                      color: '#7cb5ec'
                    }]
            },
            {
              name: '实际',
              data: res[1].map(v => parseFloat(v.Percentage)),
              zoneAxis: 'x',
              zones: [{
                      value: point3,
                      color: '#000000',
                      }, {
                      value: point4,
                      color: '#90ed7d'
                      },{
                      color: '#000000'
                    }]
            }
          ]
        };
        if(IsTopUpAvailable==1)
        {
        this.chart2 =null;
        } 

      });
    },
    getDataSource1() {
      let self = this;
      const { StartTime, EndTime } = this.form;
      var svc = this.$http('PortfolioManagement', '[TrustManagement].[usp_GetPaymentScheduleAggregationDateByTrustId]');
      svc.AddParam({Name: 'StartTime', Value:StartTime,DBType: 'string'});
      svc.AddParam({Name: 'EndTime', Value: EndTime,DBType: 'string'});
      svc.AddParam({
        Name: 'TrustID',
        Value: this.TrustID,
        DBType: 'int'
      });
      return svc.ExecTable();
    },
    getDataSource2() {
      let self = this;
      const { StartTime, EndTime } = this.form;
      var svc = this.$http('PortfolioManagement', '[TrustManagement].[usp_GetFactAssetPoolPaymentDateByTrustId]');
      svc.AddParam({
        Name: 'TrustID',
        Value: this.TrustID,
        DBType: 'int'
      });
      svc.AddParam({Name: 'StartTime', Value:StartTime,DBType: 'string'});
      svc.AddParam({Name: 'EndTime', Value: EndTime,DBType: 'string'});
      return svc.ExecTable();
    },
    stateFormat(row, column, cellValue) {
      if (cellValue == null || cellValue === '') {
        return '';
      } else {
        cellValue += '';
        if (!cellValue.includes('.')) cellValue += '.';
        return cellValue
          .replace(/(\d)(?=(\d{3})+\.)/g, function($0, $1) {
            return $1 + ',';
          })
          .replace(/\.$/, '');
      }
    }
  }
};
