result:
.advance-filter {
  height: 100%;
  border-right: 1px solid $--color-grey-border;
  &-header {
    height: 39px;
    line-height: 39px;
    border-bottom: 1px solid $--color-grey-border;
    .left{

      i{
        font-size: 14px;
        margin-right: 5px;
        color: $--color-primary;
      }
    }
  }

  .condition{
    line-height: 20px;
    font-size: 12px;
  }
  .clear-condition{
    font-size: 12px;
  }
  .filter-button {
    font-weight: 500;
    color: #777;
    padding: 0;

    i.icon-searchlist {
      font-size: 25px;
      vertical-align: -4px;
    }

    &.is-unfold,
    &:hover {
      color: $--color-primary;
    }
  }

  &-body{
    padding: 10px 10px 10px 20px;
    padding-left: 0;
    font-size: 12px;
    height: calc(100% - 47px);
    overflow: auto;
    padding-right: 0;

    /deep/ >.el-scrollbar {
      height: 100%;
      .el-scrollbar__wrap {
        overflow-x: hidden;
      }
    }
  }

  .chosen-fields {
    display: inline-block;
    margin-bottom: 10px;
    margin-right: 10px;
    background-color: #fff;
    border:1px solid $--color-grey-light;
    color: $--color-primary;
    border-radius: 3px;
    font-size: 12px;
    position: relative;
    padding-right: 22px;
    &:hover{
      border-color: $--color-primary;
      .field-remove{
        background-color: $--color-primary;
        color: #fff;

      }
    }
    .field-titles {
      margin-right: 5px;
      display: inline-block;
      padding: 3px 0 3px 10px;
      .title{
        color: $--color-black;
      }
    }

    .field-remove {
      display: inline-block;
      height: 100%;
      cursor: pointer;
      padding:0 5px;
      position: absolute;
      top: 0;right: -1px;
      line-height: 26px;
    }
  }

  .filter-condition-row {
    margin-bottom: 5px;
    padding-right: 15px;
    &:last-child {
      border-bottom: none;
      padding-bottom: 0;
      margin-bottom: 0;
    }
  }

  .filter-label {
    min-width: 100px;
    font-weight: bold;
    color: $--color-black;
    margin-bottom: 10px;
  }

  .filter-title {
    font-weight: 500;
    font-size: 16px;
    color: grey;
    /*$--color-gray;*/
    display: inline-block;
    margin-right: 10px;
  }

  .filter-tag {
    height: 26px;
    line-height: 24px;
    margin: 0 10px 5px 0;
    padding: 0 10px;
    border-color: $--color-grey-border;
    border-radius: 3px;
    color: $--color-grey;

    &:hover {
      border-color: $--color-primary;
      color: $--color-primary;
      cursor: pointer;
    }

    &.active {
      border-color: $--color-primary;
      color: #fff;
      background-color: $--color-primary;
    }
  }

  .more{
    margin-top: 5px;
    margin-right: 8px;
  }

  .spea-center{
    text-align: center;
    line-height: 26px;
  }

}

.loading-preload{
  width: 35%;
  height: 20px;
  margin-bottom: 10px;
  border-radius: 2px;
  &:nth-child(2) {
    width: 60%;
  }
  &:nth-child(3) {
    width: 42%;
  }
}
.date-interval{
  /deep/ .el-input--suffix .el-input__inner{
    padding-right: 10px;
  }
}
