  import fsvc from '@/api/FileService';
  import csvc from "@/api/CommonService.js";
  import dynamicTab from '@/mixins/dynamicTab.js';
  import {addClass,removeClass} from '@/utils/dom.js'
  import {
    mapState, mapMutations
  } from 'vuex';

  export default {
    data() {
      return {
        productDetail: {},
        docList: [],
        docListDeepCopy:[],
        updateDialog: false,
        progressSw: false,
        percentage: 0,
        progressText: '正在保存文件，请稍等',
        fileType: '',
        dialogUploadFileName: '', //上传文件名
        filterValue:''
      }
    },
    mixins: [dynamicTab],
    created() {
      this.initProductDoc()

    },
    computed: {
      TrustID() {
        return this.$store.state.productInfo.trustId;
      },
      ...mapState('user', ['userName']),
    },
    watch: {
      TrustID(v) {
        this.initProductDoc()
      },
      updateDialog(v) {
        if (v) {
          this.fileType = ''
          this.dialogUploadFileName = ''
        }
      },
      filterValue(v) {
        this.docList = JSON.parse(JSON.stringify(this.docListDeepCopy))
        if (v.trim() != '') {
          for (var i = 0; i < this.docList.length; i++) {
            this.docList[i].children = this.docList[i].children.filter((item) => {
              if (item.FileName.indexOf(v)>-1) {
                return item
              }
            })
          }
        }
      }
    },
    methods: {
      ...mapMutations('dynamicTabs', ['setTab']),
      clearFilter() {
        this.filterValue = ''
      },
      foldFun(index) {
        var dis = document.getElementsByClassName('file'+index)[0].style.display
        var iconD = document.getElementsByClassName('icon'+index)[0]
        if (dis == 'none') {
          document.getElementsByClassName('file'+index)[0].style.display = 'block'
          removeClass(iconD,'icon-zhankaisvg')
          addClass(iconD,'icon-zhankai2')
        }else{
          document.getElementsByClassName('file'+index)[0].style.display = 'none'
          addClass(iconD,'icon-zhankaisvg')
          removeClass(iconD,'icon-zhankai2')
        }
      },
      initProductDoc() {
        var self = this;
        var svc = this.$http('PortfolioManagement', 'DV.TrustAssociatedDocs_Get');
        svc.AddParam({Name: 'TrustID', Value: self.TrustID, DBType: 'int'});
        svc.ExecTables().then(res => {
          for (var i = 0; i < res[0].length; i++) {
            res[0][i].FileName = res[0][i].FileCategoryName
            res[0][i].children = []
            for (var ii = 0; ii < res[1].length; ii++) {
              res[1][ii].icon = 'iconfont icon-folder-open'
              if (res[1][ii].FileCategory == res[0][i].FileCategory) {
                res[0][i].children.push(res[1][ii])
              }

            }
          }
          self.docList = JSON.parse(JSON.stringify(res[0]))
          self.docListDeepCopy = JSON.parse(JSON.stringify(res[0]))
        })
        self.setTab({
          id: 'ProductDetail' + self.TrustID,
          name: 'ProductDoc',
        });
      },
      downLoadFile(file) {
        fsvc.DownLoadFileByPath(file.FilePath + file.FileName, file.FileName, 1)
      },
      dialogFileInputs(event) {
        var file = event.target.files;
        let self = this
        if (file.length > 1) {
          this.dialogUploadFileName = ''
          for (let key in file) {
            if (key == "length") {
              self.dialogUploadFileName = self.dialogUploadFileName.substring(0, self.dialogUploadFileName.length - 1)
              return
            }
            self.dialogUploadFileName = self.dialogUploadFileName + file[key].name + ','
          }
        } else if (file.length == 1) {
          this.dialogUploadFileName = file[0].name;

        }

      },
      dialogUploadCilck(id) {
        $("#" + id).click();
      },
      reportingFilesUpload() {
        var self = this;
        var postData = $("#excelUploads")[0].files;
        if (self.fileType == '') {
          self.$message.warning('请选择文件类型');
          return
        }
        if (postData.length < 1) {
          self.$message.error('请选择上传的文件');
          return;
        }

        self.updateDialog = false;
        var len = postData.length
        var ai = 0
        self.percentage = 0
        //for (var i = 0; i < postData.length; i++) {
        //(function (i,len) {
        var postDataI = 0;
        var nbprogress = 0;
        var postDataName = postData[postDataI].name;
        var folderName = 'DealViewer\\TrustAssociatedDoc\\' + self.$store.state.productInfo.productDetail.TrustCode + '\\' + self.fileType;
        const progress = function (progressEvt) {
          self.progressSw = true

          nbprogress = progressEvt.loaded / progressEvt.total;
          // self.progressText = '上传文件进度'+parseInt(nbprogress)+'%'
          self.percentage = parseInt((postDataI + nbprogress) / (postData.length) * 100)

        };
        var callback1 = function (fpath) {
          self.progressText = ''
          let filePath = fpath.UploadResult;
          filePath = filePath.substr(0,filePath.lastIndexOf('/')+1)
          let fileType = postDataName.substr(postDataName.lastIndexOf(".") + 1);
          var svc = self.$http('PortfolioManagement', 'DV.TrustAssociatedDocs_Add');
          svc.AddParam({
            Name: "TrustId",
            Value: self.TrustID,
            DBType: "int"
          });
          svc.AddParam({
            Name: "FileCategory",
            Value: self.fileType,
            DBType: "string"
          });
          svc.AddParam({
            Name: "FilePath",
            Value: filePath,
            DBType: "string"
          });
          svc.AddParam({
            Name: "FileName",
            Value: postDataName,
            DBType: "string"
          });
          svc.AddParam({
            Name: "FileType",
            Value: fileType,
            DBType: "string"
          });
          svc.AddParam({
            Name: "UserName",
            Value: self.userName,
            DBType: "string"
          });
          svc.ExecResult().then(res => {
			  res = parseInt(res);
            if (isNaN(res)) {
              self.$message.error('文件上传发生内部服务错误');
              self.progressSw = false
            } else {

              self.progressSw = true
              // self.percentage = parseInt(((postDataI+1)/(postData.length)) * 100)
              if (postDataI == postData.length - 1) {
                self.progressText = '已成功上传' + (postDataI + 1) + '个文件'
                setTimeout(() => {
                  self.progressSw = false
                  self.progressText = ''
                }, 500)

              }
              postDataI++

              if (postDataI < postData.length) {
                postDataName = postData[postDataI].name;
                fsvc.UploadOther(postData[postDataI], postDataName, folderName, 1)(progress, callback1)
              }

              self.initProductDoc();


            }
          });
        }
        fsvc.UploadOther(postData[postDataI], postDataName, folderName, 1)(progress, callback1)

        //})(i,len)

        //}
      },
      deleteFile(data) {
        var removeName = data.FileCategoryName + '—' + data.FileName
        var self = this
        this.$confirm(removeName + '文件将被删除,是否继续？', '提示', {
          confirmButtonText: '确定',
          cancelButttonText: '取消',
          type: 'warning'
        }).then(() => {
          var svc = self.$http('PortfolioManagement', 'DV.TrustAssociatedDocs_Remove');
          svc.AddParam({
            Name: "ReportDocumentID",
            Value: data.TrustDocumentID,
            DBType: "int"
          });
          svc.AddParam({
            Name: "UserName",
            Value: self.userName,
            DBType: "string"
          });
          svc.AddParam({
            Name: "FileCategory",
            Value: data.FileCategory,
            DBType: "string"
          });
          svc.AddParam({
            Name: "FilePath",
            Value: data.FilePath,
            DBType: "string"
          });
          svc.AddParam({
            Name: "FileName",
            Value: data.FileName,
            DBType: "string"
          });
          svc.AddParam({
            Name: "TrustID",
            Value: self.TrustID,
            DBType: "int"
          });
          svc.ExecResult().then(res => {
            if (isNaN(res)) {
              self.$message.error('文件删除请求发生错误');
            } else {
              self.$message.success('文件删除成功');
              //删除物理文件
              fsvc.Remove(data.FilePath, 1);
              self.initProductDoc();
            }
          })
        }).catch(() => {
        })

      },
    }
  }
