result:
  .login-page{
    width: 100%;
    height: 100vh;
    overflow: auto;
    padding-right: 15px;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    &-content{
      display: flex;
      flex: 1;
      align-items: center;
      justify-content: center;
      height: 100%;
    }
  }
  .dv-logo{
    height: 42px;
  }
  .cardWarp{
    width: 700px;
    display: flex;
    box-shadow: 0 6px 13px rgba(50, 63, 148, 0.985);
    border-radius: 8px;
    position: relative;
  }
  .login-card{
    width: 350px;
    height: 370px;
    background-color: #fff;
    border-radius: 8px;
    border-top-left-radius: 0;
    border-bottom-left-radius: 0;
    position: relative;
    perspective: 1500px;
    .login-card-wrapper{
      transform-style: preserve-3d;
      transition: all 1s ease;
      transform-origin: left;
      background-color: #fff;
      position: absolute;top: 0;
      z-index: 9;
      width: 100%;
      height: 100%;
      opacity: 1;
      border-radius: 8px;
    }
    .transZ{
      transform: rotateY(-180deg);
      opacity: 0;
    }
    &-wrapper{
      padding: 40px;
    }
    .el-form-item--small.el-form-item{
      margin-bottom: 17px;
    }
  }
  .rightName{
    color: #fff;
    padding-left: 30px;
    border-radius: 0;
    border-top-left-radius: 8px;
    border-bottom-left-radius: 8px;
    background: linear-gradient(-60deg,rgba(#593ce6,1) 24%,rgba(#5264D9,0.2)  );
    h4{
      font-size: 28px;
      margin-top: 10px;
    }
    h2{
      font-size: 28px;
      margin-top: 120px;
    }
  }

  .login-logo{
    text-align: center;
    line-height: 50px;
    height: 50px;
    color: $--color-primary;
    font-weight: normal;
    font-size: 20px;
  }
.registerDom {
    width: 100%;
    padding: 15px 25px!important;
    background-color: #fff;
    border-radius: 8px;
    z-index: 1!important;
    .el-form-item--small.el-form-item{
      margin-bottom: 17px;
    }
    .el-form-item__label{
      font-size: 12px;
    }
    .login-logo{
      height: 10px;
      line-height: 0;
      font-size: 16px;
    }
  }
  .login-button{
    width: 100%;
  }
