result:
  .app-header{
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    display: flex;
    justify-content: space-between;
    flex-direction: column;
    width: 100%;
    height: 40px;
    background: linear-gradient(to right , #2D3EAB, #0D1E8A 20%, #0D1E8A 70%, #3d4fc5);
    background-color: #2D3EAB;
    .inner{
      padding: 0 20px;
      .content{
        display: flex;
        height: 40px;
        flex-direction: row;
        align-items: center;
        >div{
          flex:1;
        }
        .logo{
          flex:1;
          font-size: 18px;
          font-weight: bold;
          color: #fff;
          a{
            color: #fff;
            .dv-logo{
              height: 26px
            }
          }
        }
        .menu{
          ul{
            text-align: right;
            li{
              display: inline-block;
              font-size: 14px;
              color: rgba(255,255,255,0.6);
              margin-left: 40px;
              vertical-align: middle;

              &:first-child{
                margin-left: 0;
              }
              &.message{
                &.active{
                  position: relative;
                  &:before{
                    display: inline-block;
                    width: 5px;
                    height: 5px;
                    border-radius: 5px;
                    background-color: red;
                    content: '';
                    position: absolute;
                    top: -2px;right: -2px;
                  }
                }
                i{
                  cursor: pointer;
                }
              }
              a{
                color: rgba(255,255,255,0.6);
                vertical-align: middle;
                &.router-link-active{
                  color: #fff;
                }
              }
              &.userName{
                span{
                  vertical-align: middle;
                }
                img{
                  width: 23px;
                  height: 23px;
                  vertical-align: middle;
                  margin-right: 10px;
                }
              }
            }
          }

          .setting{
            color: rgba(255,255,255,0.6);
            cursor: pointer;
            &:hover{
              color: #fff;
            }
          }
        }

      }

    }
  }
.icon-guanzhu1{
  color:rgb(247, 186, 42);
  font-size:13px;
  cursor: pointer;
}
.icon-guanzhu{
  color: #c0c4cc;
  font-size: 13px;
  cursor: pointer;
}
.moreP{
  font-size: 12px;
  text-align: center;
  line-height: 30px;
  color: $--color-grey;
  cursor: pointer;
}
.popoverUl{
  a{
    line-height: 30px;
    font-size: 12px;
    border-bottom: 1px solid #ddd;
    color: $--color-grey;
    display: inline-block;
    overflow: hidden;
    text-overflow:ellipsis;
    white-space: nowrap;
    width: 100%;
    cursor: pointer;
  }
}
.messageTab{
  font-size: 12px;
}
/deep/ .el-tabs__item{
  font-size: 12px;
  height: 30px;
  line-height: 30px;
}
